sourcesContent:
import { events, users, participants, locations } from "../../data.json";
import { uuid } from "uuidv4";
const Mutation = {
  // ! Event
  createEvent: (parent, { data }) => {
    const event = { id: uuid(), ...data };
    events.push(event);
    pubsub.publish("eventCreated", event); // ! Subscription
    return event;
  },
  updateEvent: (parent, { id, data }) => {
    const event_index = events.findIndex((event) => event.id === parseInt(id));
    if (event_index === -1) {
      throw new Error("User not found");
    }
    const update_Event = (events[event_index] = {
      ...events[event_index],
      ...data,
    });
    pubsub.publish("eventUpdated", update_Event); // ! Subscription
    return update_Event;
  },
  deleteEvent: (parent, { id }) => {
    const event_index = events.findIndex((event) => event.id === parseInt(id));
    if (event_index === -1) {
      throw new Error("Event not Found");
    }
    const deletedEvent = events[event_index];
    events.splice(event_index, 1);
    pubsub.publish("eventDeleted", deletedEvent); // ! Subscription
    return deletedEvent;
  },
  deleteAllEvents: () => {
    const length = events.length;
    events.splice(0, length);

    return {
      count: length,
    };
  },

  // ! User
  createUser: (parent, { data }) => {
    const user = { id: uuid(), ...data };
    users.push(user);
    pubsub.publish("userCreated", user); // ! Subscription
    return user;
  },
